import matplotlib.pyplot as pltimport osimport jsondata_folder = "data/animals"countDict = {}for folder in os.listdir(data_folder):    folder_name = folder    image_count = 0    for image in os.listdir(data_folder + "/" + folder_name):        image_count = image_count + 1    countDict[folder_name] = image_countif os.path.exists("count.json"):    os.remove("count.json")with open('count.json', 'w') as f:    json.dump(countDict, f)