from PIL import Imageimport osimport numpydata_folder = "data/animals"image_sizes = {}for folder in os.listdir(data_folder):    folder_name = folder    for image in os.listdir(data_folder + "/" + folder_name):        image_loc = data_folder + "/" + folder_name + "/" + image        image_sizes[image] = Image.open(image_loc, 'r').sizeprint("max", max(image_sizes.values()))print("min", min(image_sizes.values()))